import Vue from 'vue'
import Router from 'vue-router'


Vue.use(Router)

export default new Router({

    routes: [{
            path: '/app',
            name: 'App',
            component: () =>
                import ('../App.vue')
        },
        { //登录
            path: '/',
            name: 'Login',
            component: () =>
                import ('../components/Login.vue')
        },
        { //注册
            path: '/register',
            name: 'Register',
            component: () =>
                import ('../components/Register.vue')
        },
        { //主页
            path: '/home',
            name: 'Home',
            component: () =>
                import ('../components/mainPage.vue'),
            meta: {
                requireAuth: true, // 添加该字段，表示进入这个路由是需要登录的
            },
        },
        { //我的关注
            path: '/myFollow',
            name: 'myFollow',
            component: () =>
                import ('../components/myFollow.vue'),
            meta: {
                requireAuth: true, // 添加该字段，表示进入这个路由是需要登录的
            },
        },
        { //我的消息
            path: '/myMessage',
            name: 'myMessage',
            component: () =>
                import ('../components/myMessage.vue'),
            meta: {
                requireAuth: true, // 添加该字段，表示进入这个路由是需要登录的
            },
        },
        { //个人中心
            path: '/PersonalCenter',
            name: 'PersonalCenter',
            component: () =>
                import ('../components/PersonalCenter.vue'),
            meta: {
                requireAuth: true, // 添加该字段，表示进入这个路由是需要登录的
            },
        },
        { //搜索
            path: '/search',
            name: 'search',
            component: () =>
                import ('../components/search.vue'),
            meta: {
                requireAuth: true, // 添加该字段，表示进入这个路由是需要登录的
            },
        },
        { //文本编辑器
            path: '/writer',
            name: 'writer',
            component: () =>
                import ('../components/writer.vue'),
            meta: {
                requireAuth: true, // 添加该字段，表示进入这个路由是需要登录的
            },
        },

    ],
    mode: "history"
})
