import Vue from 'vue'
import Router from 'vue-router'
import menu from '../components/menu.vue'
import Login from '../components/Login.vue'
import Register from '../components/Register.vue'
import mainPage from '../components/mainPage.vue'
import myFollow from '../components/myFollow.vue'
import myMessage from '../components/myMessage.vue'
import PersonalCenter from '../components/PersonalCenter.vue'
import search from '../components/search.vue'
import writer from '../components/writer.vue'

Vue.use(Router)

export default new Router({

  routes: [
    {//登录
      path: '/',
      name: 'Login',
      components: {
        left:Login,
      }
    }, 
    {//注册
      path: '/register',
      name: 'Register',
      components: {
        top:menu,
        left:Register,
      }
    },
    {//主页
      path:'/home',
      name:'Home',
      components: {
        top:menu,
        left:mainPage,
      },
      meta: {
        requireAuth: true,  // 添加该字段，表示进入这个路由是需要登录的
      },
    },
    {//我的关注
      path:'/myFollow',
      name:'myFollow',
      components: {
        top:menu,
        left:myFollow,
      },
      meta: {
        requireAuth: true,  // 添加该字段，表示进入这个路由是需要登录的
      },
    },
    {//我的消息
      path:'/myMessage',
      name:'myMessage',
      components: {
        top:menu,
        left:myMessage,
      },
      meta: {
        requireAuth: true,  // 添加该字段，表示进入这个路由是需要登录的
      },
    },
    {//个人中心
      path:'/PersonalCenter',
      name:'PersonalCenter',
      components: {
        top:menu,
        left:PersonalCenter,
      },
      meta: {
        requireAuth: true,  // 添加该字段，表示进入这个路由是需要登录的
      },
    },
    {//搜索
      path:'/search',
      name:'search',
      components: {
        top:menu,
        left:search,
      },
      meta: {
        requireAuth: true,  // 添加该字段，表示进入这个路由是需要登录的
      },
    },
    {//文本编辑器
      path:'/writer',
      name:'writer',
      components: {
        left:writer,
      },
      meta: {
        requireAuth: true,  // 添加该字段，表示进入这个路由是需要登录的
      },
    },

  ],
  mode: "history"
})
