import Vue from 'vue'
import Router from 'vue-router'


Vue.use(Router)

export default new Router({
  routes: [
    {
      path:'/app',
      name:'App',
      component:() => import('../App.vue')
    },
    {//登录
      path: '/',
      name: 'Login',
      component: () => import('../components/Login.vue')
    }, 
    {//注册
      path: '/register',
      name: 'Register',
      component:() => import('../components/Register.vue')
    },
    {//主页
      path:'/home',
      name:'Home',
      component:() => import('../components/mainPage.vue')
    },
    {//我的关注
      path:'/myFollow',
      name:'myFollow',
      component:() => import('../components/myFollow.vue')
    },
    {//我的消息
      path:'/myMessage',
      name:'myMessage',
      component:() => import('../components/myMessage.vue')
    },
    {//个人中心
      path:'/PersonalCenter',
      name:'PersonalCenter',
      component:() => import('../components/PersonalCenter.vue')
    },
    {//搜索
      path:'/search',
      name:'search',
      component:() => import('../components/search.vue')
    },

  ],
  mode: "history"
})
