import Home from "../components/Home.vue";
export default {
  name: "app",

  data() {
    return {
      homeshow: true
    };
  },

  components: {
    homenav: Home
  },
  watch: {
    $route(to, from) {
      if (this.$route.path === "/" || "/register") {
        this.homeshow = false;
      } else {
        this.homeshow = true;
      }

      if (this.$route.path === "/bug" || this.$route.path === "/chart" || this.$route.path === "/plan" || this.$route.path === "/usecase") {
        this.homeshow = true;
      }
    }

  }
};
