    };
  },

  methods: {
    jumpToArticle() {
      router.push({
        path: "/article"
      });
    }

  },

  findArticle() {
    let url = 'http://localhost:8082/searchArticleByName2';
    axios.post(url, {
      key_word: this.article_name
    }).then(res => {
      listData.splice(0, listData.length);
      console.log(res.data);

      for (let i = 0; i < res.data.length; i++) {
        axios.post(url, {
          article_id: res.data[i].article_id
        }).then(res1 => {
          console.log();
          listData.push({
            avatar: require('/src/images/' + res.data[i].article_cover),
            type: 'star-o',
            text: res1.data.shares,
            type2: 'like-o',
            text2: res1.data.likes,
            type3: 'message',
            text3: res1.data.comment,
            title: res.data[i].article_title,
            description: res.data[i].date,
            content: res.data[i].article_introduction,
            topic: res.data[i].topic
          });
        });
      }
    });
  },

  components: {
    router
  }
};
